import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import {Table,Button,Container,Modal,ModalBody, ModalHeader,FormGroup,ModalFooter} from 'reactstrap';

const data =[
  {id: 1, Nombre: "cealmeba", Numero: "2711903613"},
  {id: 2, Nombre: "holaceal", Numero: "2711903613"},
  {id: 3, Nombre: "smskmskd", Numero: "2711903613"},
  {id: 4, Nombre: "kkdkdfdk", Numero: "2711903613"},
  {id: 5, Nombre: "kkddfkdf", Numero: "2711903613"},
  {id: 6, Nombre: "mmffdfdd", Numero: "2711903613"},
];
class App extends React.Component {
  state={
    data: data,
    form:{
      id:'',
      nombre:'',
      numero:''
    },
   modalInsertar: false,
  };
  handlechange=e=>{
   this.setState({
     form:{
       ...this.state.form,
       [e.targe.name]: e.targe.value,
     }
   })
   mostrarModalInsertar=()=>{
    this.setState({mo})
   }
  
  }
  render() {
    return (
      <>
      <Container>
      <br/>
        <Button color="primary">Insertar Nuevo Personaje</Button>
         <br/> <br/>
         <Table>
           <thead><tr><th>Id</th>
           <th>Nombre</th>
           <th>Telefono</th>
           <th>Acciones</th> </tr></thead>
           <tbody>
             {this.state,data.map((elemento)=>(
              <tr>
                 <td>{elemento.id}</td>
                 <td>{elemento.Nombre}</td>
                 <td>{elemento.Numero}</td>
                 <td><Button color ="primary">Editar</Button></td>
                 <td><Button color ="danger">Eliminar</Button></td>
              </tr>
             ))}
           </tbody>
         </Table>
        </Container>
        <Modal isOpen={this.state.modalInsertar}>
          <ModalHeader>
            <div>
              <h3>Insertar Registro</h3>
            </div>
          </ModalHeader>
          <ModalBody>
            <FormGroup>
              <label>Id:</label>
              <imput className="form-control" readOnly type="text" value={this.state.data.length+1}/>
            </FormGroup>

            <FormGroup>
              <label>Nombre</label>
              <input className='form-control' name="Nombre" type="text" onChange={this.handlechange}/>
            </FormGroup>

            <FormGroup>
              <label>Nombre</label>
              <input className='form-control' name="Numero" type="text" onChange={this.handlechange}/>
            </FormGroup>

          </ModalBody>
        </Modal>
      </>)
  }
}

export default App;
